                        <path d="M222.990036,59.75 C226.303744,65.4895115 224.337242,72.8285963 218.597731,76.1423048 L196.08107,89.1423048 C190.341559,92.4560133 183.002474,90.4895115 179.688766,84.75 C176.375057,79.0104885 178.341559,71.6714037 184.08107,68.3576952 L206.597731,55.3576952 C212.337242,52.0439867 219.676327,54.0104885 222.990036,59.75 Z" class="pip" id="pip2"></path>
                        <path d="M179.25,16.0099642 C184.989511,19.3236727 186.956013,26.6627576 183.642305,32.4022691 L170.642305,54.9189296 C167.328596,60.6584411 159.989511,62.6249429 154.25,59.3112344 C148.510489,55.9975259 146.543987,48.6584411 149.857695,42.9189296 L162.857695,20.4022691 C166.171404,14.6627576 173.510489,12.6962557 179.25,16.0099642 Z" class="pip" id="pip1"></path>
                    </g>
                </svg>
                <audio class="tick-tock">
                    <source src="../assets/audio/clock-ticking.mp3" type="audio/mpeg">
                </audio> 
                 <audio class="aww">
                    <source src="../assets/audio/aww.mp3" type="audio/mpeg">
                </audio> 
            </div>
            <div class="phrase-container"></div>
        </section>
        <img class="wave wave--back" src="../assets/img/wave-level3.png" />
        <img class="wave wave--middle" src="../assets/img/wave-level2.png" />
        <img class="wave wave--front" src="../assets/img/wave-level1.png" />
    </div>
</template>

<script>
    export default {
        name: 'Game',
        created: function() {
            var self = this;
            self.$root.pipInterval;
            self.$root.timerInterval
            self.$root.counterInterval

            window.addEventListener('message',function(event) {
                console.log(event);
                if(event.origin !== window.location.origin) return;
                if(event.data === "correct") {
                    self.correctAnswer(event.data);
                }
                else if (event.data === "saver") {
                    self.runScreenSaver(event.data);
                }
                else if (event.data === "game") {
                    self.backToGame(event.data);
                }
                else {
                    self.restart();
                    let message = event.data;
                    if(message.word === undefined) {
                        let counterContainer = document.getElementsByClassName('counter-container')[0]
                        counterContainer.classList.add('hide')
                        return;
                    };
                    
                    console.log(message.word)
                    seperateWord(message.word);
                    createTitle(message.type);
                    const logoContainer = document.getElementsByClassName('logo-container')[0];
                    logoContainer.classList.add('hide');

                    function seperateWord(w) {
                        let letters = w.split('');
                        createCards(letters);
                    }

                    function createCards(l) {
                        var container = document.getElementsByClassName('phrase-container')[0];
                        container.classList.remove('long-word');
                        container.classList.remove('over10');
                        container.classList.remove('over13');
                        container.classList.remove('has-spaces');
                        container.classList.remove('two-lines');
                        if(container) {
                            let spaceCount = 0;
                            let cardCount = 0;
                            let phraseLength = l.length;
                            for(let letter of l) {
                                if(letter === " ") {
                                    spaceCount++;
                                    cardCount++;
                                    let card = document.createElement("div");
                                    card.classList.add("card");
                                    card.classList.add("space");
                                    if(spaceCount === 2 && cardCount > 8 && phraseLength - cardCount > 4) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 2 && phraseLength - cardCount > 7) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 3 && cardCount < 8){
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 1 && cardCount === 9 && phraseLength - cardCount === 8) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 1 && cardCount === 8 && phraseLength - cardCount === 7) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 1 && cardCount === 11 && phraseLength - cardCount === 7) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 1 && cardCount === 9 && phraseLength - cardCount === 7) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }
                                    else if(spaceCount === 1 && cardCount === 12) {
                                        card.classList.add('break');
                                        container.classList.add('two-lines');
                                    }

                                    container.appendChild(card);
                                    container.classList.add('has-spaces');
                                }
                                else {
                                    cardCount++;
                                    let card = document.createElement("div");
                                    card.classList.add("card");
                                    container.appendChild(card);
                                    let cardBack = document.createElement("div");
                                    cardBack.classList.add("card-back");
                                    card.appendChild(cardBack);
                                    let cardFront = document.createElement("div");
                                    cardFront.classList.add('front');
                                    card.appendChild(cardFront);
                                    let p = document.createElement("p");
                                    p.innerHTML = letter;
                                    cardFront.appendChild(p);
                                }

                                if(l.length > 13) {
                                    container.classList.add('over13');
                                }
                                else if(l.length > 10) {
                                    container.classList.add('over10');
                                }

                                if(spaceCount === 0 && cardCount === 16) {
                                    container.classList.add('long-word');
                                    container.classList.remove('over13');
                                }
                            }
                        }
                    }
                    function createTitle(t) {
                        const catagoryContainer = document.getElementsByClassName('catagory-container')[0];
                        catagoryContainer.getElementsByTagName('h2')[0].innerHTML = t;
                        if (t === "photography") {
                            catagoryContainer.classList.remove('elements');
                            catagoryContainer.classList.add('photography');
                        }
                        else if (t === "graphic elements") {
                            catagoryContainer.classList.remove('photography');
                            catagoryContainer.classList.add('elements');
                        }
                        else {
                            catagoryContainer.classList.remove('photography');
                            catagoryContainer.classList.remove('elements');
                        }
                    }
                    self.countdownOverlay();
                }
            },false);
        },
        methods: {
            flipCard: function(e) {
                let cards = document.getElementsByClassName('card');
                let cardsArray = [];
                if(e && e == 'correct') {
                    for(let card of cards) {
                        card.classList.add('flipped');
                    }
                    return;
                }
                else {
                    for(let card of cards) {
                        if(card.classList.contains('space')) {

                        }
                        else {
                            cardsArray.push(card);
                        }
                    }
                    let interval = (12000 / cardsArray.length);
                    setInterval(function() {
                        if(cardsArray.length > 0) {
                            let card = cardsArray.splice(Math.floor(Math.random()*cardsArray.length), 1);
                            card[0].classList.add('flipped');
                        }
                    }, interval);
                }
            },
            startPips: function(e) {
                let self = this;
                const timerContainer = document.getElementsByClassName('timer-container')[0];
                const svg = timerContainer.getElementsByTagName('svg')[0];
                let pips = svg.getElementsByClassName('pip');
                let pipsArray = [];
                var correct = false;

                for(let pip of pips) {
                    pipsArray.push(pip);
                }
                this.$root.pipInterval = setInterval(function() {
                    if(pipsArray.length > 0) {
                        let pip = pipsArray.pop();
                        pip.classList.add('hide');
                    }
                }, 1000);
            },
            stopPips: function () {
                let self = this;
                clearInterval(self.$root.pipInterval);
            },
            startTimer: function() {
                let self = this
                const timerContainer = document.getElementsByClassName('timer-container')[0];
                let second = '12';
                let timer = timerContainer.getElementsByTagName('h2')[0];
                this.$root.playClock = document.getElementsByClassName('tick-tock')[0];
                this.$root.playClock.play();
                this.$root.timerInterval = setInterval(function() {
                    if(second > 0) {
                        second--;
                        timer.innerHTML = second;
                    }
                    else {
                        self.$root.playClock.pause()
                        self.$root.playClock.currentTime = 0
                        let aww = document.getElementsByClassName('aww')[0]
                        aww.loop = false
                        aww.play()
                    }
                }, 1000);
            },
            stopTimer: function() {
                let self = this;
                this.$root.playClock.pause();
                this.$root.playClock.currentTime = 0;
                clearInterval(self.$root.timerInterval);
            },
            countdownOverlay: function() {
                let self = this;
                let waves = document.getElementsByClassName('wave');
                const mainGame = document.getElementsByClassName('main-game')[0];
                const counterContainer = document.getElementsByClassName('counter-container')[0];
                var beep = document.getElementsByClassName('beep')[0];

                for(let wave of waves) {
                    wave.classList.add('blur');
                }
                mainGame.classList.add('blur');

                let counter = document.getElementsByClassName('counter')[0];
                let second = 5;

                counterContainer.classList.remove('hide');
                this.$root.counterInterval = setInterval(function() {
                    if(second > 1) {
                        beep.play();
                        second--;
                        if(second === 3) {
                            counter.innerHTML = 'Ready';
                        }
                        else if(second === 2) {
                            counter.innerHTML = 'Set';
                        }
                        else if(second === 1) {
                            counter.innerHTML = 'Go';
                        }
                        else {
                            counter.innerHTML = second;
                        }
                    }
                    else {
                        counterContainer.classList.add('hide');
                        for(let wave of waves) {
                            wave.classList.remove('blur');
                        }
                        mainGame.classList.remove('blur');
                        self.startGame();
                        self.stopCounter();
                        window.opener.postMessage("count down complete", window.parent.location.origin);
                    }
                }, 1000);
            },
            stopCounter: function() {
                let self = this;
                clearInterval(self.$root.counterInterval);
            },
            startGame: function() {
                let self = this;
                let phrase = document.getElementsByClassName('phrase-container')[0];
                phrase.classList.add('position');
                if(phrase.classList.contains) {
                    self.flipCard();
                    self.startPips();
                    self.startTimer();
                }
            },
            correctAnswer: function(e) {
                const counterContainer = document.getElementsByClassName('counter-container')[0];
                const phraseContainer = document.getElementsByClassName('phrase-container')[0];
                let waves = document.getElementsByClassName('wave');
                let mainGame = document.getElementsByClassName('main-game')[0];

                var l = e
                this.flipCard(l);
                this.stopPips();
                this.stopTimer();
                if(counterContainer.classList.contains('hide')) {

                }
                else {
                    this.stopCounter();
                    counterContainer.classList.add('hide');
                    phraseContainer.classList.add('position');
                    for(let wave of waves) {
                        wave.classList.remove('blur');
                    }
                    mainGame.classList.remove('blur');
                }
            },
            runScreenSaver: function() {
                var screenSaver = document.getElementsByClassName('screen-saver')[0];
                var video = screenSaver.getElementsByTagName('video')[0];
                screenSaver.classList.remove('hide');
                video.play();
            },
            backToGame: function() {
                let self = this;
                var screenSaver = document.getElementsByClassName('screen-saver')[0];
                const logoContainer = document.getElementsByClassName('logo-container')[0];
                const counterContainer = document.getElementsByClassName('counter-container')[0];
                if(screenSaver.classList.contains('hide')) {
                    logoContainer.classList.remove('hide');
                    self.restart();
                    counterContainer.classList.add('hide');
                }
                else {
                    var video = screenSaver.getElementsByTagName('video')[0];
                    screenSaver.classList.add('hide');
                    logoContainer.classList.remove('hide');
                    self.restart();
                    counterContainer.classList.add('hide');
                    video.pause();
                }
            },
            restart: function() {
                let self = this;
                var phrase = document.getElementsByClassName('phrase-container')[0];
                var cards = phrase.getElementsByClassName('card');
                var timerContainer = document.getElementsByClassName('timer-container')[0];
                var timer = timerContainer.getElementsByTagName('h2')[0];
                var timerSvg = timerContainer.getElementsByTagName('svg')[0];
                var pips = timerSvg.getElementsByClassName('pip');
                const counterContainer = document.getElementsByClassName('counter-container')[0];
                let counter = document.getElementsByClassName('counter')[0];

                counterContainer.classList.remove('hide');
                if(cards.length > 0) {
                    while (phrase.hasChildNodes()) {
                        phrase.removeChild(phrase.lastChild);
                    }
                    phrase.classList.remove('position');
                    self.stopTimer();
                    self.stopCounter();
                    timer.innerHTML = '12';
                    counter.innerHTML = '5';
                    self.stopPips();

                    for(let pip of pips) {
                        pip.classList.remove('hide');
                    }
                }
            }
        }
    }
</script>
<style lang="scss" scope>
    $site-orange: #FBC27E;
    $site-orange-dark: #E89A4B;
    $site-orange-light: #FCC37D;
    $site-white: #FFFFFF;
    $site-gray: #58595B;

    $t: .3s;

    @font-face {
        font-family:'DIN Black';
        src: url('/src/assets/font/DIN Black.eot');
        src: url('/src/assets/font/DIN Black.eot?#iefix') format('embedded-opentype'),
            url('/src/assets/font/DIN Black.woff2') format('woff2'),
            url('/src/assets/font/DIN Black.woff') format('woff'),
            url('/src/assets/font/DIN Black.svg#DIN Black') format('svg');
        font-weight: 900;
        font-style: normal;
        font-stretch: normal;
        unicode-range: U+0020-02DC;
    }
    * {
        margin: 0;
        padding: 0;
    }
   html {
        overflow: hidden;
    }
    .blur {
        filter: blur(10px);
    }
    .hide {
        opacity: 0;
    }
    .game-screen {
        width: 100vw;
        height: 100vh;
        background: radial-gradient(ellipse, $site-orange-light , $site-orange, $site-orange-dark );

        .screen-saver {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            transition: $t;
            z-index: 6;

            &.hide {
                opacity: 0;
            }
            video {
                position: fixed;
                top: 50%;
                left: 50%;
                min-width: 100%;
                min-height: 100%;
                width: auto;
                height: auto;
                z-index: -100;
                transform: translateX(-50%) translateY(-50%);
            }
        }
        .logo-container {
            position: absolute;
            z-index: 3;
            width: 100vw;
            height: 100vh;
            background: radial-gradient(ellipse, $site-orange-light , $site-orange, $site-orange-dark );
            transition: $t;

            .logo {
                position: absolute;
                left: 50%;
                top: 50%;
                margin: 0 auto;
                width: 60%;
                height: 60%;
                transform: translate(-50%, -60%);

                svg {
                    width: 100%;
                    height: 100%;
                }
            }
        }
        .counter-container {
            position: absolute;
            width: 100vw;
            height: 100vh;
            z-index: 5;
            transition: $t;
            background: rgba($site-orange-dark, .6);

            .counter-box {
                position: absolute;
                width: 80%;
                height: 480px;
                top: 50%;
                left: 50%;
                background: url('/src/assets/img/halftone.png');
                transform: translate(-50%, -50%);
            }
            .counter {
                position: absolute;
                top: 50%;
                left: 50%;
                font-size: 225px;
                color: $site-white;
                transform: translate(-50%, -50%);
                font-family: 'DIN Alternate';
            }
        }
        .main-game {
            display: flex;
            position: relative;
            justify-content: space-between;
            align-items: flex-start;
            flex-wrap: wrap;
            width: 100vw;
            height: 100vh;

            .headline-container {
                position: relative;
                width: 680px;
                height: 250px;

                img {
                    position: absolute;
                    width: 680px;
                    height: 250px;
                    top: 28px;
                    left: 0;
                }
                h2 {
                    position: absolute;
                    top: 119px;
                    left: 266px;
                    font-family: 'DIN Alternate';
                    font-size: 44px;
                    text-transform: uppercase;
                    color: $site-gray;
                }
            }
            .timer-container {
                position: relative;
                margin-top: 28px;
                margin-right: 48px;
                width: 250px;
                height: 250px;
                text-align: center;
                background-image: url('../assets/img/timer-shadow.png');
                background-repeat: no-repeat;
                background-position: 50% 50%;
                background-size: contain;

                h2 {
                    position:relative;
                    top: 50%;
                    font-family: 'DIN Alternate', sans-serif;
                    font-size: 104px;
                    color: $site-gray;
                    transform: translateY(-55%);
                }
                svg {
                    position: absolute;
                    right: 0;
                    top: -5px;

                    .pip {
                        transition: $t;
                        &.hide {
                            opacity: 0;
                        }
                    }
                }
            }
            .phrase-container {
                display: flex;
                justify-content: center;
                flex-wrap: no-wrap;
                margin: 0 auto;
                width: 90%;
                perspective: 1000px;
                transform: translateY(150%) scale(.5);
                transition: transform 0.5s;

                &.position {
                    transform: translateY(-50%) scale(1);
                }
                &.long-word {
                    .card {
                        width: calc(100%/18);
                    }
                }
                &.two-lines.position {
                    transform: translateY(-30%) scale(1);
                    .card {
                        margin-bottom: 10px;
                    }
                }
                &.over13 {
                    flex-wrap: wrap;

                    .card {
                        width: calc(100%/16);
                        font-size: 100px;
                    }
                }

                &.over10 {
                    .card {
                        width: calc(100%/12);
                        font-size: 125px;
                    }
                }
                .card {
                    position: relative;
                    margin: 0 5px;
                    width: 180px;
                    height: 250px;
                    background: $site-white;
                    color: $site-gray;
                    font-family: 'DIN Alternate', sans-serif;
                    font-size: 144px;
                    text-align: center;
                    text-transform: uppercase;
                    transform-style: preserve-3d;
                    transition: transform 0.8s cubic-bezier(0.175, 0.885, 0.32, 1.275);

                    &.space {
                        opacity: 0;
                        width: 90px;
                    }
                    &.flipped {
                        transform: rotateY(180deg);
                    }
                    div {
                        position: absolute;
                        width: 100%;
                        height: 100%;
                        backface-visibility: hidden;

                        &.card-back {
                            background: $site-gray;
                        }
                        p {         
                            position: absolute;                   
                            top: 50%;
                            left: 50%;
                            transform: translate(-50%, -50%) scaleX(-1);

                            &.over10 {
                                font-size: 100px;
                            }
                        }
                    }
                }
                .break {
                    flex-basis: 100%;
                    width: 0;
                    height: 0;
                }
            }
        }
        .wave {
            position: absolute;
            width: 105%;
            left: 50%;
            bottom: -5px;
            z-index: 4;
            animation-name: roll;
            animation-duration: 3s;
            animation-iteration-count: infinite;
            animation-timing-function: ease-in;
            transform-origin: 50% 100%;
            transform: translateX(-50%) translateY(0px);

            &--front {
                bottom: -100px;
            }
            &--middle {
                bottom: -90px;
                animation-delay: .4s;
            }
            &--back {
                bottom: -65px;
                animation-delay: .7s;
            }
        }
    }

    @keyframes roll {
        0% {
            transform: translateX(-49%) translateY(0px);
            animation-timing-function: ease-in;
        }
        25% {
            transform: translateX(-50%) translateY(5px);
            animation-timing-function: ease-out;
        }
        50% {
            transform: translateX(-51%) translateY(0px);
            animation-timing-function: ease-in;
        }
        75% {
            transform: translateX(-50%) translateY(-5px);
            animation-timing-function: ease-out;
        }
        100% {
            transform: translateX(-49%) translateY(0px);
            animation-timing-function: ease-in;
        }
    }

</style>
